    function _create(address _seller, bytes32 _red, bytes32 _blue) internal returns (uint256 tokenId){
        Token memory token = Token({
            seller: _seller,
            red: _red,
            blue: _blue,
            redParticipant: new address[](0),
            blueParticipant: new address[](0),
